import itertools

class Solution:
    def isPowerOfTwo(self, n):
        while n%2 == 0:
            n /= 2
        return 1 == n

    def getAllPermutations(self, n):
        permutations = []
        for num in itertools.permutations(list(n)):
            permutations.append(int("".join(num)))
        return permutations

    def reorderedPowerOf2(self, n: int) -> bool:
        n = str(n)
        for num in self.getAllPermutations(n):
            if(self.isPowerOfTwo(num) and len(n) == len(str(num))): return True
        return False
