class Solution {
   public:
    bool isScramble(string s1, string s2) {
        int n = s1.size();
        bool dp[n][n][n + 1];
        memset(dp, 0, sizeof dp);
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (s1[i] == s2[j]) {
                    dp[i][j][0] = true;
                }
            }
