            for (int i = 0; i <= n - len; i++) {
                for (int j = 0; j <= n - len; j++) {
                    for (int len1 = 1; len1 <= len - 1; len1++) {
                        int len2 = len - len1;
                        dp[i][j][len - 1] |= dp[i][j][len1 - 1] && dp[i + len1][j + len1][len2 - 1];
                        dp[i][j][len - 1] |= dp[i][j + len2][len1 - 1] && dp[i + len1][j][len2 - 1];
                    }
                }
            }
        }
        return dp[0][0][n - 1];
