    const trackKey = s1 + s2;
    if (trackKey in trackMap) return !!trackMap[trackKey];

    let result = false;
    let xorFW = 0;
    let xorBW = 0;

    for (var i = 0, j = s1.length - 1, iPlus = 1; i < s1.length - 1; i++, j--, iPlus++) {
