    int dp[32][32][32];
    int  help(string& s1,string&s2,int len,int s1s,int s2s){

        //understand each of the recursive state of this function
        //what is the base case 
        //we just need to check 
        if(s1.substr(s1s,len)==s2.substr(s2s,len)){
            return 1;
        }

        if(dp[s1s][s2s][len]!=-1){
            return dp[s1s][s2s][len];

        }



        //trying all partitions in the current recursive call
        int curr=0;

        for(int k=1;k<len;k++){
            //in each iteration we will breaking after the kth char 
            int c1=help(s1,s2,k,s1s,s2s);
            int c2=help(s1,s2,len-k,s1s+k,s2s+k);
            int c3=help(s1,s2,k,s1s,s2s+len-k);
            int c4=help(s1,s2,len-k,s1s+k,s2s);

            if((c1 && c2)||(c3 && c4)){
                curr=1;
                break;
            }
        }
        return dp[s1s][s2s][len]=curr;

    }
