        @cache
        def f(a, b):
            if a == b:
                return True
            assert(len(a) == len(b))

            if len(a) == 1:
                return False

            for length in range(1, len(a)):
                # no swap
                if f(a[:length], b[:length]) and f(a[length:], b[length:]):
                    return True

                # swap
                if f(a[:length], b[-length:]) and f(a[length:], b[:len(a)-length]):
                    return True
            return False

        return f(s1, s2)


