            return true;
        }
        if (s1.length() != s2.length()) {
            return false;
        }
        int len = s1.length();
        boolean[][][] map = new boolean[len + 1][len + 1][len + 1];
        return helper(s1, 0, s2, 0, len, map);
    }

    public boolean helper(String s1, int i, String s2,int j, int len, boolean[][][] map) {
        if (len == 0) { return true; }
        if (len == 1 && s1.charAt(i) == s2.charAt(j)) {
            map[i][j][len] = true;
        }
        if (map[i][j][len]) {
            return true;
        }
        for (int k = 1; k < len; k++) {
            if (helper(s1, i, s2, j, k, map) && helper(s1, i + k, s2, j + k, len - k, map)) {
                return map[i][j][len] = true;
            }
            if (helper(s1, i, s2, j + len - k, k, map) && helper(s1, i + k, s2, j, len - k, map)) {
                return map[i][j][len] = true;
            }
        }
        return map[i][j][len] = false;
    }
