    public boolean helper(String s1, int i, String s2,int j, int len, int[][][] map) {
        if (len == 0) { return true; }
        if (len == 1 && s1.charAt(i) == s2.charAt(j)) {
            map[i][j][len] = 1;
        }
        if (map[i][j][len]!=0) {
            return map[i][j][len]==1;
        }
        for (int k = 1; k < len; k++) {
            if (helper(s1, i, s2, j, k, map) && helper(s1, i + k, s2, j + k, len - k, map)) {
                map[i][j][len] = 1;
                return true;
            }
            if (helper(s1, i, s2, j + len - k, k, map) && helper(s1, i + k, s2, j, len - k, map)) {
                map[i][j][len] = 1;
                return true;
            }
        }
        map[i][j][len] = 2;
        return false;
