        int n = s1.length();
        boolean[][][] dp = new boolean[n + 1][n + 1][n + 1];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                dp[i][j][1] = s1.charAt(i) == s2.charAt(j);
            }
        }

        for (int len = 2; len <= n; len++) {
            for (int i = 0; i <= n - len; i++) {
                for (int j = 0; j <= n - len; j++) {
                    for (int k = 1; k <= len; k++) {
                        boolean left = dp[i][j][k] && dp[i + k][j + k][len - k];
                        boolean right = dp[i][j + len - k][k] && dp[i + k][j][len - k];
                        if (left || right) {
                            dp[i][j][len] = true;
                        }
                    }
                }
            }
        }

        return dp[0][0][n];
    }
