class Solution(object):
    def isScramble(self, s1, s2):
        """
        :type s1: str
        :type s2: str
        :rtype: bool
        """

        if len(s1) != len(s2):
            return False
        n = len(s1)
        dp = [[[False for _ in range(n+1)] for _ in range(n)] for _ in range(n)]
        for k in range(n+1):
            for i in range(n):
                for j in range(n):
                    if k == 0:
                        dp[i][j][k] = True
                        continue
                    if k == 1:
                        dp[i][j][k] = s1[i] == s2[j]
                        continue
                    if i + k > n or j + k > n:
                        continue
                    for p in range(1, k):
                        dp[i][j][k] |= dp[i][j][p] and dp[i+p][j+p][k-p]
                        dp[i][j][k] |= dp[i][j+p][k-p] and dp[i+k-p][j][p]
        return dp[0][0][n]

```

