public:

    map<pair<string,string>,bool> mp;

    bool dos(string s1,string s2){

        if(s1==s2){      //if strings are equal return true;
            return 1;
        }
        if(s1.size()==1){  //this line will be executed when above statement is not true
            return 0;       // so if length of s1 and s2 is 1 and they are not equal return false
        }
        if(mp.find({s1,s2})!=mp.end()){ //map to memoize, stored as pairs, if found no need to go further.
            return mp[{s1,s2}];
        }
        int n=s1.size();

        //Now we will compare by partioning the string at each i,1,2,3,...n-1.
        // and check if after flipping or without flipping both strings are equal or not.

        for(int i=1;i<n;i++){

            //when there is no flip left part of first string will be compared by left part of second string
            // and right part of first string will be compared by right part of second string

            bool no_flip=dos(s1.substr(0,i),s2.substr(0,i))&&dos(s1.substr(i),s2.substr(i));

            //if it comes out to true store it in map;
            if(no_flip){
                return mp[{s1,s2}]=1;   
            }

            //when there is flip left part of first string will be compared to right part of second string;
            //and right part of second string will be compared with left part of second string.

            bool flip=dos(s1.substr(0,i),s2.substr(n-i))&&dos(s1.substr(i),s2.substr(0,n-i));
            if(flip){
               return mp[{s1,s2}]=1;
            }
            // NOTE: we are not executing else part as it may happen that after further partioning 
            //they come out to be equal


        }
        return mp[{s1,s2}]=0;
    }


    bool isScramble(string s1, string s2) {

        return dos(s1,s2);
    }
