// recursive approach - gives TLE


class Solution {
public:

    bool scramble(string s, string t){
        int n= s.size();

        if(s==t)
            return true;
        vector<int> cnt(26,0);
        for(int i=0; i<n; i++)
            cnt[s[i]-'a']++, cnt[t[i]-'a']--;

        for(int i=0; i<26; i++)
            if( cnt[i] )
                return false;

        // s=gr|eat  t=gr|eat   (not scrambled)
        // s=gr|eat  t=ate|gr   (scrambled)
        for(int len=1; len<n; len++){

            if( scramble(s.substr(0,len), t.substr(0,len)) and scramble(s.substr(len), t.substr(len)))
                return true;
            if( scramble(s.substr(0,len), t.substr(n-len)) and scramble(s.substr(len), t.substr(0,n-len)))
                return true;
        }        
        return false;
    }
    bool isScramble(string s, string t) {

        return scramble(s,t);
    }
};
