unordered_map<string,bool> dp;
    bool isScramble(string a, string b) {
        if(size(a)!=size(b)) return false;
        int n=size(a);
        string s=a+" "+b;
        if(dp.count(s)) return dp[s];
        if(a==b) return dp[s]=true;
        for(int len=1;len<n;len++){
            bool ans=isScramble(a.substr(0,len),b.substr(0,len)) and isScramble(a.substr(len),b.substr(len));
            if(ans) return dp[s]=true;
            ans=isScramble(a.substr(0,len),b.substr(n-len)) and isScramble(a.substr(len),b.substr(0,n-len));
            if(ans) return dp[s]=true;
        }
        return dp[s]=false;
    }
