class Solution {
public:
    bool isScramble(string s1, string s2) {
        int sum1 = 0, sum2 = 0;
        int n = s1.size();
        if (n == 1) return s1[0] == s2[0];
        int p1 = -1, p2 = -1;
        for (int i = 0; i < n; i++) {
            sum1 += s1[i] * s1[i] - s2[i] * s2[i];
            sum2 += s1[i] * s1[i] - s2[n - 1 - i] * s2[n - 1 - i];
