class Solution {
    public boolean solve(String s1, String s2, HashMap<String, Boolean> map){

        // batawo compareTo use kiya hai (bade log ) : matlab v dekho iska "compares two strings lexicographically"
        // acche se bole to sort karo aur check karo....
        if(s1.compareTo(s2) == 0){
            return true;
        }

        //idhar v padhoge
        int n = s1.length();

        // ab single length ho gya to kaahe ko iterate karna choro ka base base apna y
        if(n<=1){
            return false;
        }

        // ab ynha dono string ko concatenate karo maine to underscore liya hai tumhe jo lena hai le lo
        // kya hi farak padne wala hai
        // par isiliy liy taaki recursion m same chizze baare baar calculate na ho simple
        String key = s1 + "_" + s2;

        // agar y already process ho gya to sidha iska value fetch karo aur return karaa do
        if(map.containsKey(key)){
            return map.get(key);
        }


        //idhar m ek flag liya hai taaki future m isi par khell sko
        boolean flag = false;

        // idhar second element se khelna start karo aur last tak khelnaa...
        for(int i=1;i<n;i++){

            // y ratt lo easy hai.. bas aage piche, piche aage, aage aage, piche piche compare karna hia bas...
            if((solve(s1.substring(0,i), s2.substring(n-i, n), map) && (solve(s1.substring(i, n), s2.substring(0, n-i), map))) || 
               ((solve(s1.substring(0,i), s2.substring(0,i), map)) && (solve(s1.substring(i, n), s2.substring(i, n), map)))){
                // idhar flag true kra aur break kar do agge test hi nhi karna hia
                   flag = true;
                   break;
               }
        }

        // suru m bola tha recusion m chize repeat hoti hai to ynha uss repetation ko khtm kar do 
        map.put(key, flag);
        // y rhha final answer...
                return flag;
    }

    // bas bakchodi karni hai inhe. simple sala sort kar do and match kara lo ki dono match kar rhe hain ya nhi. par nhi inko karni hai bakiti to chalo suru karte hain inke tarike se.
    public boolean isScramble(String s1, String s2) {

        // ynha pe length check kar lo, ki same hai ya nhi. means string s1 and s2 dene wala ko bewkuff hoga wrong length dega...
        if(s1.length() != s2.length()){return false;}
        // phir ynhi check kar lo ki same hain ya nhi : same hain to diya hi kaahe be...
        if(s1.equals(s2)) return true;

        //ab hashmap create karo taaki har chota chota part ko match karaa kar k dekhana hia ki same hai ki nhi matalab pura time pass karna hai map ko
        HashMap<String, Boolean> map = new HashMap<>();
        // ynha se suru hoti hai khichadi par acchi waali... bole to recursion.
        return solve(s1, s2, map);
    }
}
