class Solution {
public:
    unordered_map<string,int> mp;
    bool isScramble(string s1, string s2) {
        if (s1.length() != s2.length()) {  //recursive approach will give TLE
        return false;
    }

    int n = s1.length();

    // Empty strings are scramble strings
    if (n == 0) {
        return true;
    }

    // Equal strings are scramble strings
    if (s1 == s2) {
        return true;
    }
    string key = (s1 + " " + s2);
    if(mp.find(key)!=mp.end())
    return mp[key];

    bool flag=false;
    for(int i=1;i<=n-1;i++){      
       if (isScramble(s1.substr(0, i), s2.substr(0, i)) //first condition
            && isScramble(s1.substr(i, n - i), s2.substr(i, n - i)) || (isScramble(s1.substr(0, i), //second condition
                       s2.substr(n - i, i))
            && isScramble(s1.substr(i, n - i),
                          s2.substr(0, n - i)))) {
            flag =true;
            return true;
        }

    }
    mp[key]=flag;
    return false;
    }
};
