class Solution {
public:
    bool isScramble(string s1, string s2) {
        int n1 = s1.size(), n2 = s2.size();
        if (n1 != n2)   return false; // lengths should be the same
        if (s1 == "" && s2 == "")   return true; // empty strings are scramble of each other
        // try to write dp state params
		// an obvious dp state would be dp[start1][end1][start2][end2]
        // but as the length of two strings should always be equal we can determine end2 using start1, end1, and start2
        // thus, we use dp[start1][start2][len] as our state
        bool dp[n1 + 1][n1 + 1][n1 + 1]; // denoting dp[start1][start2][len]
        memset(dp, false, sizeof(dp));
        // if we have to build a solution from bottom up first try to think how to get the answers for the base cases
        // here as our base case we want asnwers for all substrings of s1 * all substrings of s2 of size 1
        // that means we already have 3 loops for len, ind_i and ind_2
        // now why len loop is outside because we first want to solve all the subproblems of len 1 before moving to a higher len
        for(int l = 1; l <= n1; l++){ 
            for (int i = 0; i + l - 2 < n1; i++) {
                for (int j = 0; j + l - 2 < n1; j++) {
                    dp[i][j][l] = (s1.substr(i, l) == s2.substr(j, l));
                    // transactions are a bit clearer from the question itself
                    // we just have to decide the left and right parts for scrambling
                    // thus we have dp[i][j][l] |= (dp[i][j][k] && dp[i + k][j + k][l - k]) || (dp[i + l - k][j][k] && dp[i][j + k][l - k]);
