    bool solve(string str, string &s2, int start, int end){
        if (start == end){
            return s2[start] == str[0];
        }
        if (mp.find({str, start+end}) != mp.end())  return mp[{str, start+end}];

        bool ans = 0;
        for (int i = 0;i < end-start;i++){
            bool left = solve(str.substr(0, i+1), s2, start, start+i) && solve(str.substr(i+1), s2, start+i+1, end);
