            if (ans){
                mp[{str, start+end}] = ans;
                return ans;
            }
        }
        mp[{str, start+end}] = ans;
        return ans;
    }

    bool isScramble(string s1, string s2) {
        return solve(s1, s2, 0, s1.size()-1);
    }
};
