            boolean ans2 = helper(str1.substring(0, i), str2.substring(n-i)) 
                && helper(str1.substring(i), str2.substring(0, n-i));

            if(ans1 || ans2){
                ans = true;
                break;
            }
        }
        map.put(str1 + "&" + str2, ans);
        return ans;
    }

