int CheckSramble(char *current, char *target, int len)
{
    char *flag=s_lookup+((current-s_s1)*s_len+target-s_s2)*s_len+len;
    if(*flag!=-1) return *flag;
    if(!memcmp(current, target, len)) return *flag=1;
    int char_table_s1_head[26]={};
