        char_table_s2_tail[target[len-i]-'a']++;
        if(!memcmp(char_table_s1_head, char_table_s2_tail, sizeof(char_table_s1_head)))
            if(CheckSramble(current+i, target, len-i) && CheckSramble(current, target+len-i, i))
                return *flag=1;
