from itertools import product
class Solution:
    def isScramble(self, s1: str, s2: str) -> bool:
        lt = len(s1) # len(s1) == len(s2)
        # 3-D DP matrix
        dp = [[[False]*(lt + 1) for _ in range(lt)] for _ in range(lt)]
		# Initialize DP matrix
        for i, j in product(range(lt), range(lt)):
            dp[i][j][1] = s1[i] == s2[j]

        for k in range(2, lt + 1):
            for i in range(lt - k + 1):
                for j in range(lt - k + 1):
                    for w in range(1, k):
                        # 后半段dp[i][j+k-w][w] and dp[i+w][j][k-w]比较抽象，建议在纸上画出来
                        if (dp[i][j][w] and dp[i+w][j+w][k-w]) or (dp[i][j+k-w][w] and dp[i+w][j][k-w]):
                            dp[i][j][k] = True
                            break
        return dp[0][0][lt]
```
