        l = len(s1)
        if l < 4 or s1 == s2: return True
        f = self.isScramble
        for i in range(1, l):
            if f(s1[:i], s2[:i]) and f(s1[i:], s2[i:]) or \
                f(s1[:i], s2[-i:]) and f(s1[i:], s2[:-i]):
