class Solution:
    def __init__(self):
        self.dic = {}
    def isScramble(self, s1: str, s2: str) -> bool:
        k = (s1, s2)
        if k in self.dic: return self.dic[k]
        if sorted(s1) != sorted(s2):
            self.dic[k] = False
            return False
        l = len(s1)
        if l < 4 or s1 == s2:
            self.dic[k] = True
            return True
        f = self.isScramble
        for i in range(1, l):
            if f(s1[:i], s2[:i]) and f(s1[i:], s2[i:]) or \
                f(s1[:i], s2[-i:]) and f(s1[i:], s2[:-i]):
                self.dic[k] = True
                return True
        self.dic[k] = False
        return False
