    Map<String, Boolean> memo = new HashMap<>();
    public boolean isScramble(String s1, String s2) {
        if(s1.equals(s2))
            return true;
        if(!equalByFreq(s1, s2))
            return false;

        String key = s1+s2;
        if(memo.containsKey(key))
            return memo.get(key);

        int len1 = s1.length(), len2 = s2.length();
        boolean match = false;
        for(int i = 1; i < s1.length(); i++){
            String p1 = s1.substring(0,i);
            String p2 = s1.substring(i);

            match = isScramble(p1, s2.substring(0, i)) && isScramble(p2, s2.substring(i));
            if(match)
                break;
            match = isScramble(p1, s2.substring(len2-i)) && isScramble(p2, s2.substring(0, len2-i));
            if(match)
                break;
        }

        memo.put(key, match);
        return match;
    }

    public boolean equalByFreq(String s1, String s2){
        int xor = 0;
        for(int i= 0; i < s1.length(); i++){
            xor ^= (s1.charAt(i)-'a')^(s2.charAt(i)-'a');
        }
        return xor == 0;
    }
