class Solution {
public:
   unordered_map<string,bool>DP;

bool solve(string s1,string s2){

    if(s1.compare(s2)==0)return 1;                  
    if(s1.size()<=1)return 0; 

    string key=s1+" "+s2;
    if(DP.find(key)!=DP.end())
        return DP[key];

    bool ans=0;
    int n=s1.size();

    for(int i=1;i<n;i++)
    {
        bool temp1=solve(s1.substr(0,i),s2.substr(n-i,i)) && solve(s1.substr(i,n-i),s2.substr(0,n-i));

        bool temp2=solve(s1.substr(0,i),s2.substr(0,i)) && solve(s1.substr(i,n-i),s2.substr(i,n-i));

        ans=temp1||temp2;

        if(ans==1)
            break;

    }

    return DP[key]=ans;
}

bool isScramble(string s1, string s2) 
{
    if(s1.length()!=s2.length())
        return 0;
    if(s1.length()==0)
        return 1;

    return solve(s1,s2);    
}
};
