        return isScramble(s1, 0, length - 1, s2, 0, length - 1);
    }

    private boolean isScramble(String s1, int startIdx1, int endIdx1, String s2, int startIdx2, int endIdx2) {
        if(startIdx1 == endIdx1) {return s1.charAt(startIdx1) == s2.charAt(startIdx2);}

        int i = startIdx1, j = startIdx2, count = 0;
        int[] counts = new int[128];
		// find substrings that contain the same characters
		// you can refer to problem 76. Minimum Window Substring to find out the trick here
        do {
            if(counts[s1.charAt(i++)]++ >= 0) {count++;}
            if(counts[s2.charAt(j++)]-- > 0) {count--;}
        } while(i < endIdx1 && count != 0);
        if(count == 0 && isScramble(s1, startIdx1, i - 1, s2, startIdx2, j - 1) &&
           isScramble(s1, i, endIdx1, s2, j, endIdx2)) {return true;}

        int i2 = startIdx1, j2 = endIdx2, count2 = 0;
        Arrays.fill(counts, 0);
        do {
            if(counts[s1.charAt(i2++)]++ >= 0) {count2++;}
            if(counts[s2.charAt(j2--)]-- > 0) {count2--;}
        } while(i2 < endIdx1 && count2 != 0);
        if(count2 == 0  && isScramble(s1, startIdx1, i2 - 1, s2, j2 + 1, endIdx2) &&
           isScramble(s1, i2, endIdx1, s2, startIdx2, j2)) {return true;}

        return false;
    }
}
