class Solution {
public:
    bool isScramble(string s1, string s2) {
        if(s1 == s2) return true; //ending condition
        int head = 0, size = s1.length();
        vector<int> s1_v(128,0),s2_front(128,0),s2_back(128,0);
        int any = 0;
        while(head < size-1){
            s1_v[s1[head]]++;
            s2_front[s2[head]]++;
            head++;
            s2_back[s2[size-head]]++;
            if(s1_v == s2_front)
                any |= isScramble(s1.substr(0,head),s2.substr(0,head)) & isScramble(s1.substr(head),s2.substr(head));
            else if(s1_v == s2_back)
                any |= isScramble(s1.substr(0,head),s2.substr(size-head)) & isScramble(s1.substr(head),s2.substr(0,size-head));
            if(any) //if any == false, this head position cannot make it happen. we need to go ahead to check next cutting index.
			    return true;
        }
        return any;
    }
};
