                    if ((dp[k][i][j] && dp[len - k][i + k][j + k]) || (dp[k][i][j + len - k] && dp[len - k][i + k][j])) {
                        dp[len][i][j] = true;
                        break;
                    }
                }
            }
        }
    }
    return dp[n][0][0];
}

