class Solution {
public:
    void merge(vector<int>& nums1, int m, vector<int>& nums2, int n) {

        int p1 = m-1;      //size of first array
        int p2 = n-1;      // size of second array
        int i =n+m-1;      //size of first array having lenth is sum of two array

        while(p2>=0){      //edge case if first array doesnot cannot any element 

            if(p1>=0 && nums1[p1]>nums2[p2]){   //if greaterthen second array element than copy to last index of first array 
              nums1[i--]=nums1[p1--];
            }
            else{
                nums1[i--]=nums2[p2--];
            }
        }

    }
};

