class Solution {
    public void merge(int[] nums1, int m, int[] nums2, int n) {
        int newArr[] = new int[m+n];

        int i = 0, j = 0, k = 0;
        while(i != m && j != n) {
            if(nums1[i] < nums2[j]) {
                newArr[k++] = nums1[i++];
            }
            else {
                newArr[k++] = nums2[j++];
            }
        }

        while(i != m) {
            newArr[k++] = nums1[i++];
        }

        while(j != n) {
            newArr[k++] = nums2[j++];
        }

        for(k = 0; k < m+n; k++) {
            nums1[k] = newArr[k];
        }
    }
}

