                nums1[k++] = nums1[i++];
            }
            else{
                nums1[k++] = nums2[j++];
            }
        }

        while(i<m){
            nums1[k++] = nums1[i++]; 
        }

        while(j<n){
            nums1[k++] = nums2[j++];
        }
    }
};



