class Solution {
public:

    bool Valid(int i,int j,int n,int m){
        if(i<0 || j<0 || j>=m || i>=n) return false;
        return true;
    }
    vector<vector<int>> spiralMatrixIII(int n, int m, int x, int y) {
        vector<vector<int>> ans ;
        ans.push_back({x,y});   //initial point
        int len=1;
        while(ans.size()<n*m){              //Base Case --> once we get whole grid --> break
            for(int j=1;j<=len;j++){        //going right 
                if(Valid(x,y+j,n,m)){       //Checking if this pos exists in Grid
                    ans.push_back({x,y+j});
                }
            }
            y+=len;                         //Updating the value of y coordinate 
            for(int i=1;i<=len;i++){        //Going Down
                if(Valid(x+i,y,n,m)) ans.push_back({x+i,y});        //Checking if exists in Grid
            }
            x+=len;                         //Updating X coordinate 
            len++;          //Increasing length of step in each direction 
            for(int j=1;j<=len;j++){        //Going Left 
                if(Valid(x,y-j,n,m)) ans.push_back({x,y-j});
            }
            y-=len;
            for(int i=1;i<=len;i++){        //Going Up
                if(Valid(x-i,y,n,m)) ans.push_back({x-i,y});
            }
            x-=len;
            len++;
        }
        return ans;
    }
};

