	public int[][] spiralMatrixIII(int R, int C, int r0, int c0) {
        int[][] ret = new int[R*C][2];
        int step = 1;
        int i = 0;
        int r = r0;
        int c = c0;
        int offset = 1;
        while (i < R*C) {
