/**
 * @param {number} rows
 * @param {number} cols
 * @param {number} rStart
 * @param {number} cStart
 * @return {number[][]}
 */
 var spiralMatrixIII = function(rows, cols, rStart, cStart) {
    let r = rStart, c = cStart, results = [];
    results.push([r,c]);
    // Draw a picture of an infinite grid, then highlight a smaller grid inside with ${rows} of rows and ${cols} of columns.
    // Draw a little man at the rStart row and the cStart column.
    // We will tell the man to walk a number of steps in a certain direction.
    // if the man is on a highlighted square, he will collect it.
    const walk = (steps,rStep,cStep) => {
        for (let i =0;i<steps;i++) {
            r +=rStep, c+=cStep;
            if ( r>=0 && c >=0 && r < rows && c<cols) results.push([r,c]);
        }
