class Solution(object):
    def spiralMatrixIII(self, R, C, r0, c0):
        """
        :type R: int
        :type C: int
        :type r0: int
        :type c0: int
        :rtype: List[List[int]]
        """

        def nxt(r,c):
            d=1
            yield (r,c)
            while True:
                for i in range(d):
                    c+=1
                    yield (r,c)
                for i in range(d):
                    r+=1
                    yield (r,c)
                for i in range(d+1):
                    c-=1
                    yield (r,c)
                for i in range(d+1):
                    r-=1
                    yield (r,c)
                d+=2

        ret=[]
        r,c=r0,c0
        for (r,c) in nxt(r0,c0):
            if 0<=r<R and 0<=c<C:
                ret.append([r,c])
            if len(ret)==R*C:
                break

        return ret

```

