class Solution:
    def spiralMatrixIII(self, rows: int, cols: int, rStart: int, cStart: int) -> List[List[int]]:
        visits = []
        top, bottom, left, right = rStart, rStart + 1, cStart, cStart + 1

        self.incr = 1
        direction = 0

        def place(row, col):
            visits.append([row, col])
            self.incr += 1

        while self.incr != (1 + rows * cols):
            if direction == 0:
                if top >= 0:
                    row = top
                    for col in range(max(left, 0), min(right, cols)):
                        place(row, col)
                right += 1
            if direction == 1:
                if right <= cols:
                    col = right - 1
                    for row in range(max(top, 0), min(bottom, rows)):
                        place(row, col)
                bottom += 1
            if direction == 2:
                if bottom <= rows:
                    row = bottom - 1
                    for col in range(min(right - 1, cols - 1), max(left - 1, -1), -1):
                        place(row, col)
                left -= 1
            if direction == 3:
                if left >= 0:
                    col = left
                    for row in range(min(bottom - 1, rows - 1), max(top - 1, -1), -1):
                        place(row, col)
                top -= 1
            direction = (direction + 1) % 4
        return visits
