    public int[][] spiralMatrixIII(int rows, int cols, int rStart, int cStart) {

 /*     
There are rows X cols elements in Matrix , and each of that element we have to
traverse in clockwise spiral i.e Jalebi , and we need to print co-ordinates of each
element , so we need rows = rows X cols and in each 1D Array , there are two indices
only i.e (row , col) , so we need cols = 2
*/    

        mat = new int[rows * cols][2];

        int len = 1;
        int r = rStart, c = cStart;

        while(size < rows * cols) {

            //Move Right or East , so cols will increase
            for(int i=0; i<len; i++) {
                add(r, c++,  rows , cols);
            }

            //Move Down or South , so rows will increase
            for(int i=0; i<len; i++) {
                add(r++, c, rows, cols);
            }
                    len++;  

            //Move Left or West , so cols will decrease
            for(int i=0; i<len; i++) {
                add(r, c--, rows, cols);
            }

            //Move Up or North , so rows will decrease
            for(int i=0; i<len; i++) {
                add(r--, c, rows, cols);
            }
                    len++;
        }
                    return mat;
    }
}


