class Solution {
	public int[][] spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
		// int[][] arr = new int[rows][cols];
		// arr[rStart][cStart] = 1;
		int[][] ans = new int[rows * cols][2];
		if (rows == 1 && cols == 1) return ans;

		int[] writer = new int[1];
		ans[0][0] = rStart;
		ans[0][1] = cStart;        
		writer[0] = 2;
		spiralMatrixIII(ans, 3, 0, rows, cols, rStart, cStart, writer);
		return ans;
	}

	private void spiralMatrixIII(int[][] ans, int size, int offset, int rows, int cols, int rStart, int cStart, int[] writer) {
		if ((rStart + offset >= rows) && (rStart - offset < 0) && (cStart + offset >= cols) && (cStart - offset < 0)) {
			return;
		}
		// right
		for (int i = 0; i < size - 1; i++) {
			if ((rStart - offset + i <= rows - 1) && (cStart + 1 + offset <= cols - 1) && (rStart - offset + i >= 0) && (cStart + 1 + offset >=0)){
				// arr[rStart - offset + i][cStart + 1 + offset] = writer[0];
				writer[0]++;
				ans[writer[0] - 2][0] = rStart - offset + i;
				ans[writer[0] - 2][1] = cStart + 1 + offset;
			} else {
				continue;
			}
		} 

		// bottom
		for (int i = 0; i < size - 1; i++) {
			if ((rStart + 1 + offset <= rows - 1) && (cStart - i + offset <= cols - 1) && (rStart + 1 + offset >= 0) && (cStart - i + offset >= 0)){
				// arr[rStart + 1 + offset][cStart - i + offset] = writer[0];
				writer[0]++;
				ans[writer[0] - 2][0] = rStart + 1 + offset;
				ans[writer[0] - 2][1] = cStart - i + offset;
			} else {
				continue;
			}
		}

		// left
		for (int i = 0; i < size - 1; i ++) {
			if ((rStart - i + offset <= rows - 1) && (cStart - 1 - offset <= cols - 1) && (rStart - i + offset >= 0) && (cStart - 1 - offset >= 0)){
				// arr[rStart - i + offset][cStart - 1 - offset] = writer[0];
				writer[0]++; 
				ans[writer[0] - 2][0] = rStart - i + offset;
				ans[writer[0] - 2][1] = cStart - 1 - offset;
			} else {
				continue;
			}
		}

		// top
		for (int i = 0; i < size - 1; i++) {
			if ((rStart - 1 - offset <= rows - 1) && (cStart + i - offset <= cols - 1) && (rStart - 1 - offset >= 0) && (cStart + i - offset >= 0)){
				// arr[rStart - 1 - offset][cStart + i - offset] = writer[0];
				writer[0]++;  
				ans[writer[0] - 2][0] = rStart - 1 - offset;
				ans[writer[0] - 2][1] = cStart + i - offset;
			} else {
				continue;
			}
		}
		spiralMatrixIII(ans, size + 2, offset + 1, rows, cols, rStart, cStart, writer);
	}
}

