                r0 = r0 + dirs[d][0]
                c0 = c0 + dirs[d][1]
                steps_left -= 1
                if r0 >= 0 and r0 < R and c0 >= 0 and c0 < C:
                    ans.append([r0, c0])
                elif steps_left:
                    if d == 0:
                        if c0 < 0 and r0 >= 0:
                            steps_left += c0 + 1
                            c0 = -1
                        else:
                            c0 += steps_left
                            steps_left = 0
                    elif d == 1:
                        if r0 < 0 and c0 < C:
                            steps_left += r0 + 1
                            r0 = -1
                        else:
                            r0 += steps_left
                            steps_left = 0
                    elif d == 2:
                        if c0 >= C and r0 < R:
                            steps_left -= c0 - C
                            c0 = C
                        else:
                            c0 -= steps_left
                            steps_left = 0
                    else:
                        if r0 >= R and c0 >= 0:
                            steps_left -= r0 - R
                            r0 = R
                        else:
                            r0 -= steps_left
                            steps_left = 0
            d += 1
        return ans

