        int[] steps = {1, 1}; //steps to move along c and r direction
        int r = r0, c = c0;
        res[0] = new int[] {r0, c0};
        int i = 1;
        while (i < R * C) {
            for (int j = 0; j < steps[p % 2]; j++) {
                int[] dir = dirs[p];
                r += dir[0];
                c += dir[1];
                if (r >= 0 && r < R && c >= 0 && c < C) {
                    res[i++] = new int[] {r, c};
                }
            }
            steps[p % 2]++;
            p = (p + 1) % 4;
        }
        return res; 
    }
}

