class Solution {
    public int[][] spiralMatrixIII(int R, int C, int r0, int c0) {
        int[][] result = new int[R * C][2];
        int[][] directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}}; // Right, Down, Left, Up
        int direction = 0; // Start with the right direction
        int steps = 1; // Number of steps to take in the current direction
        int count = 0; // Number of cells visited

        int x = r0;
        int y = c0;

        while (count < R * C) {
            for (int i = 0; i < steps; i++) {
                if (x >= 0 && x < R && y >= 0 && y < C) {
                    result[count][0] = x;
                    result[count][1] = y;
                    count++;
                }

                // Move in the current direction
                x += directions[direction][0];
                y += directions[direction][1];
            }

            // Change direction after every two steps
            direction = (direction + 1) % 4;

            // Increase steps after every two directions
            if (direction == 0 || direction == 2) {
                steps++;
            }
        }

        return result;
    }
}
