class Solution {
public:
    vector<vector<int>> spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        static const struct { int dRow, dCol, dSteps; } dirs[] = {
            // right,   down,      left,        up
            {0, 1, 0}, {1, 0, 0}, {0, -1, 1}, {-1, 0, 1},
        };

        vector<vector<int>> res;
        res.reserve(rows * cols);

        int row = rStart, col = cStart;
        for (int nSteps = 1; res.size() < rows * cols; nSteps += 2) {
            for (const auto& dir : dirs) {
                for (int i = 0; i < nSteps + dir.dSteps; ++i) {
                    if (row >= 0 && row < rows && col >= 0 && col < cols)
                        res.emplace_back(vector<int>{row, col});
                    row += dir.dRow;
                    col += dir.dCol;
                }
            }
        }

        return res;
    }
};
