class Solution {
public:
    vector<vector<int>> spiralMatrixIII(int rows, int cols, int rstart, int cstart) {
        vector<vector<int>> ans;
        int total = rows * cols;
        int ec = cstart, sc = cstart;
        int sr = rstart , er = rstart;

        while(ans.size() < total){
            ec++;
            if(sr >= 0) 
                for(int i = max(0,sc); i < min(cols,ec) ; i++) 
                    ans.push_back({sr,i});

            er++;
            if(ec < cols) 
                for(int i = max(sr,0) ; i < min(er,rows); i++) 
                    ans.push_back({i,ec});

            sc--;
            if(er < rows) 
                for(int i = min (ec, cols-1); i >max(-1, sc) ;i--) 
                    ans.push_back({er,i});

            sr--;
            if(sc >=0 )
                for(int i = min(er,rows-1) ;i > max(-1,sr) ; i--) 
                    ans.push_back({i,sc}); 
        }
        return ans;
    }
};
