class Solution {
    public int[][] spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        int[][] mat = new int[rows * cols][2];
        int in = 0;
        int[][] directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};
        int rs = rStart;
        int cs = cStart;
        int directionIndex = 0;
        int steps = 1;
        while (in < rows * cols) {
            for (int i = 0; i < steps; i++) {
                if (rs >= 0 && rs < rows && cs >= 0 && cs < cols) {
                    mat[in][0] = rs;
                    mat[in][1] = cs;
                    in++;
                }
                rs += directions[directionIndex][0];
                cs += directions[directionIndex][1];
            }
            if (directionIndex % 2 == 1) {
                steps++;
            }
            directionIndex = (directionIndex + 1) % 4;
        }
        return mat;
    }
}
```

