 class Solution {
public:
    vector<vector<int>> spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        vector<vector<int>> result;

        // Initialize the boundary variables
        int left = cStart - 1;
        int right = cStart + 1;
        int top = rStart - 1;
        int bottom = rStart + 1;

        // Add the initial position to the result
        result.push_back({rStart, cStart});

        // Continue until all positions are covered
        while (left > -1 || right < cols || top > -1 || bottom < rows) {
            // Traverse right from top to bottom
            if (right < cols) {
                for (int i = top + 1; i < bottom; i++) {
                    result.push_back({i, right});
                }
                right++;
            }

            // Traverse down from right to left
            if (bottom < rows) {
                for (int i = right - 1; i > left; i--) {
                    result.push_back({bottom, i});
                }
                bottom++;
            }

            // Traverse left from bottom to top
            if (left > -1) {
                for (int i = bottom - 1; i > top; i--) {
                    result.push_back({i, left});
                }
                left--;
            }

            // Traverse up from left to right
            if (top > -1) {
                for (int i = left + 1; i < right; i++) {
                    result.push_back({top, i});
                }
                top--;
            }
        }

        return result;
    }
};

