class Solution {
    public int[][] spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        int[][] result = new int[rows * cols][2];
        int[][] directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};
        int directionIndex = 0;
        int currentRow = rStart;
        int currentCol = cStart;
        int count = 0;
        int stepsInCurrentDirection = 1;

        while (count < rows * cols) {
            for (int i = 0; i < 2; i++) {  // Обробка двох кроків у поточному напрямку
                for (int j = 0; j < stepsInCurrentDirection; j++) {
                    if (currentRow >= 0 && currentRow < rows && currentCol >= 0 && currentCol < cols) {
                        result[count++] = new int[]{currentRow, currentCol};
                    }
                    currentRow += directions[directionIndex][0];
                    currentCol += directions[directionIndex][1];
                    if (count == rows * cols) {
                        return result;  // Всі позиції відвідані, повертаємо результат
                    }
                }
                directionIndex = (directionIndex + 1) % 4;  // Зміна напрямку
            }
            stepsInCurrentDirection++;  // Збільшення кількості кроків у наступному напрямку
        }

        return result;
    }
}

