    func spiralMatrixIII(_ rows: Int, _ cols: Int, _ rStart: Int, _ cStart: Int) -> [[Int]]
    {
        // coord.x is rows from top to down, coord.y is columns from left to right
        var coord = SIMD2(rStart, cStart), direction = SIMD2(0, 1)

        let origin = SIMD2(0, 0), corner = SIMD2(rows - 1, cols - 1)
        let SIMD2True = SIMDMask<SIMD2<Int>>(repeating: true)
        func isInGrid() -> Bool { 
            ((coord .>= origin) == SIMD2True) && ((coord .<= corner) == SIMD2True)
