def spiralMatrixIII(self, rows: int, cols: int, rStart: int, cStart: int) -> List[List[int]]:
    matrix = [[-1 for i in range(cols)] for j in range(rows)]
    if rows==0:
        return []
    t=rStart
    ans=[]
    b=t+1
    l=cStart
    r=l+1
    d=0
    ct=1
    while ct<=rows*cols:
        if l<=0:
            l=0
        if t<=0:
            t=0
        if r>=cols-1:
            r=cols-1
        if b>=rows-1:
            b=rows-1
        if(d==0):
            for i in range(l,r+1):
                if matrix[t][i]==-1:
                    ans.append([t,i])
                    matrix[t][i]=0
                    ct+=1
            d=1
            l-=1
        elif(d==1):
            for i in range(t,b+1):
                if matrix[i][r]==-1:
                    matrix[i][r]=0
                    ans.append([i,r])
                    ct+=1
            d=2
            t-=1
        elif(d==2):
            for i in reversed(range(l,r+1)):
                if matrix[b][i]==-1:
                    ans.append([b,i])
                    matrix[b][i]=0
                    ct+=1
            r+=1
            d=3
        elif(d==3):
            for i in reversed(range(t,b+1)):
                if matrix[i][l]==-1:
                    ans.append([i,l])
                    matrix[i][l]=0
                    ct+=1
            b+=1
            d=0
    return ans

